pragma solidity ^0.5.0;

import "./IERC721.sol";

// add is collactable item function
import "./CollectorI.sol";

import "./WalletlessI.sol";

contract ItemContract {
    struct Item {
        uint owner;
    }

    struct HeroOwned {
        uint owner;  // Items and LORD owner address
        uint[5] items;  // Item IDs
    }

    mapping(uint => Item) public itemList;
    mapping(uint => bool) public createdList;
    mapping(uint => uint) public brokerOwnedList;
    /**
     *  Nonce counter for each player
     *
     *  key:    User ID
     *  value:  Nonce
     */
    mapping(uint => uint) public nonceList;
    /**
     * Items which are assigned to the Hero Token ID, rather than to wallet address
     *
     * Locked Items holds the list of items
     */
    mapping(uint => HeroOwned) public lockedList;

    //##########################################################
    //   Events:
    //    1. ItemCreated(item ID, owner ID, burn ID)
    //    2. ItemsLocked(hero ID, items IDs, owner ID)
    //    3. ItemsUnlocked(hero ID, items IDs, owner ID)
    //    4. Granting Ownership(item ID, price, owner ID)
    //    5. Refusing Ownership(item ID, owner ID)
    //    6. Returning Ownership(item ID, new Owner ID)
    //##########################################################

    event ItemCreated(uint itemID, string ownerID, uint burnID);
    event ItemsLocked(uint heroID, uint[5] itemIDs, string ownerID);
    event ItemsUnlocked(uint heroID, uint[5] itemIDs, string ownerID);
    event OwnershipGranted(uint itemID, string ownerID);
    event OwnershipRefused(uint itemID, string ownerID);
    event OwnershipReturned(uint itemID, string ownerID);
    event ItemCollected(uint itemID, string ownerID);


    //##########################################################
    //   Contract Address:
    //    1. Setting This Contract Owner address(item ID, owner ID)
    //    2. Setting Manager address(hero ID, items IDs, owner ID)
    //    3. Setting Hero Token address(hero ID, items IDs, owner ID)
    //    4. Setting Walletless address(item ID, price, owner ID)
    //    5. Setting Broker address(item ID, price, owner ID)
    //    6. Setting Collector address(item ID, price, owner ID)
    //
    //    Digest codes:
    //      0x1902  - Set Item
    //      0x1903  - Set Initial Item Lock
    //      0x1904  - Lock
    //      0x1905  - Unlock
    //##########################################################
    uint public nonce         = 1;
    address _contractOwner    = 0xFAa502EBe96601782A34b1a947B676FB4e9d090c;
    address _heroToken        = address(0);
    address _walletless       = address(0);
    address _broker           = address(0);
    address _collector        = address(0);

    function GetDigest(address account) external returns(bytes32) {
      bytes32 digest = keccak256(abi.encodePacked(
        account,
        nonce
      ));

      return digest;
    }

    function GetSigner(address account, uint8 v, bytes32 r, bytes32 s) external returns(address) {
      bytes32 digest = keccak256(abi.encodePacked(
        account,
        nonce
      ));

      return ecrecover(digest, v, r, s);
    }

    function SetContractOwner(address contractOwner, uint8 sigV, bytes32 sigR, bytes32 sigS) external {
        bytes32 digest = keccak256(abi.encodePacked(
          contractOwner,
          nonce
        ));
        require(Verify(digest, _contractOwner, sigV, sigR, sigS), "Verification Failed");

        nonce = nonce + 1;

        _contractOwner = contractOwner;
    }
    function SetHeroToken(address heroToken, uint8 sigV, bytes32 sigR, bytes32 sigS) external {
      bytes32 digest = keccak256(abi.encodePacked(
        heroToken,
        nonce
      ));
      require(Verify(digest, _contractOwner, sigV, sigR, sigS), "Verification Failed");

      nonce = nonce + 1;

      _heroToken = heroToken;
    }
    function SetWalletless(address walletless, uint8 sigV, bytes32 sigR, bytes32 sigS) external {
      bytes32 digest = keccak256(abi.encodePacked(
        walletless,
        nonce
      ));
      require(Verify(digest, _contractOwner, sigV, sigR, sigS), "Verification Failed");

      nonce = nonce + 1;

      _walletless = walletless;
    }
    function SetBroker(address broker, uint8 sigV, bytes32 sigR, bytes32 sigS) external {
      bytes32 digest = keccak256(abi.encodePacked(
        broker,
        nonce
      ));
      require(Verify(digest, _contractOwner, sigV, sigR, sigS), "Verification Failed");

      nonce = nonce + 1;

      _broker = broker;
    }
    function SetCollector(address collector, uint8 sigV, bytes32 sigR, bytes32 sigS) external {
      bytes32 digest = keccak256(abi.encodePacked(
        collector,
        nonce
      ));
      require(Verify(digest, _contractOwner, sigV, sigR, sigS), "Verification Failed");

      nonce = nonce + 1;

      _collector = collector;
    }
    function GetNonce() external returns(uint) {
      return nonce;
    }

    //##########################################################
    //   Minting:
    //    1. Minting a new Item(owner Address, item ID, burning Item ID)
    //    .2 Minting Items as Lock items(uint[5] IDs, Hero ID)
    //##########################################################
    function SetItem(address ownerAddr, uint id, uint burnId, uint8 sigV, bytes32 sigR, bytes32 sigS) public returns(bool){
        bytes32 digest = keccak256(abi.encodePacked(
          ownerAddr,
          id,
          nonce
        ));
        require(Verify(digest, _contractOwner, sigV, sigR, sigS), "Verification Failed");

        require(id > 0,                       "Item ID must be greater than 0");
        require(createdList[id] == false,     "Item should not be created already");

        uint owner = WalletlessI(_walletless).GetNumber(ownerAddr);

        require(owner > 0,             "Player not found");

        if (burnId > 0) {
          require(itemList[burnId].owner == owner, "Item list for burning do not belong to item creator");

          delete itemList[burnId];
          delete brokerOwnedList[burnId];
        }

        itemList[id] = Item(owner);
        createdList[id] = true;

        string memory ownerID = WalletlessI(_walletless).GetID(ownerAddr);
        emit ItemCreated(id, ownerID, burnId);

        nonce = nonce + 1;

        return true;
    }
    function SetLockedItems(uint[5] memory ids, uint heroID, uint8 sigV, bytes32 sigR, bytes32 sigS) public returns (bool) {
        require(msg.sender == _contractOwner, "PERMISSION DENIED, Only Item Factory can change the Item Owner");
        require(lockedList[heroID].owner == 0, "Has Locked Items");

        for(uint i=0; i<5; i++) {
            require(createdList[ids[i]] == false, "Item exists");
        }

        address heroOwnerAddress = IERC721(_heroToken).ownerOf(heroID);
        require(heroOwnerAddress != address(0), "PERMISSION DENIED, Hero do not logged in");

        uint heroOwner = WalletlessI(_walletless).GetNumber(heroOwnerAddress);

        require(heroOwner > 0, "Hero owner is not registered");

        lockedList[heroID] = HeroOwned(heroOwner, ids);

        string memory ownerID = WalletlessI(_walletless).GetID(heroOwnerAddress);

        for(uint i=0; i<5; i++) {
            itemList[ids[i]] = Item(0);
            createdList[ids[i]] = true;
            emit ItemCreated(ids[i], ownerID, 0);
        }

        emit ItemsLocked(heroID, ids, ownerID);
    }

    //##########################################################
    //   Broker (Who takes ownership over the Item):
    //    1. Grants a permission to the Broker(address ownerAddr, item ID, signature...)
    //    2. Cancel the Grant(address ownerAddr, item ID)
    //    3. Broker Returns Item to the new Owner(address newOwner, item ID)
    //    4. Check for Item Ownership(item ID, address to compare)
    //    5. Check for Item Existence(item ID)
    //##########################################################
    function GrantOwnership(address ownerAddr, uint id, uint8 sigV, bytes32 sigR, bytes32 sigS) public {
        require(createdList[id] == true,        "Item not found");

        uint owner = WalletlessI(_walletless).GetNumber(ownerAddr);

        require(itemList[id].owner == owner,    "Item owner is not matching with given Permission granting user");

        // Signature
        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR_BACK,
            GetGivingBackHash(id, nonceList[owner])
        ));
        require(Verify(digest, ownerAddr, sigV, sigR, sigS), "Verification Failed");

        itemList[id].owner = 0;

        brokerOwnedList[id] = owner;

        nonceList[owner] = nonceList[owner] + 1;

        string memory ownerID = WalletlessI(_walletless).GetID(ownerAddr);
        emit OwnershipGranted(id, ownerID);
    }
    function RefuseOwnership(address ownerAddr, uint id, uint8 sigV, bytes32 sigR, bytes32 sigS) public {
        require(createdList[id] == true,              "ITEM NOT FOUND, Item must be on the blockchain");

        uint owner = WalletlessI(_walletless).GetNumber(ownerAddr);

        require(brokerOwnedList[id] == owner,   "Item owner is not matching with given Permission granting user");

        // Signature
        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR_REFUSE,
            GetGivingBackHash(id, nonceList[owner])
        ));
        require(Verify(digest, ownerAddr, sigV, sigR, sigS), "Verification Failed");

        delete brokerOwnedList[id];

        itemList[id].owner = owner;

        nonceList[owner] = nonceList[owner] + 1;

        string memory ownerID = WalletlessI(_walletless).GetID(ownerAddr);
        emit OwnershipRefused(id, ownerID);
    }
    function ReturnOwnership(address newOwner, uint id) public {
        require(msg.sender == _broker, "Only Broker is allowed to return ownership");
        require(createdList[id] == true, "Item not found");
        require(brokerOwnedList[id] > 0, "Broker not control Item");

        uint owner = WalletlessI(_walletless).GetNumber(newOwner);

        require(owner > 0,    "Player not found");

        delete brokerOwnedList[id];

        itemList[id].owner = owner;

        string memory ownerID = WalletlessI(_walletless).GetID(newOwner);
        emit OwnershipReturned(id, ownerID);
    }
    function IsItemOwner(uint id, address owner) external view returns(bool) {
        return itemList[id].owner != WalletlessI(_walletless).GetNumber(owner);
    }
    function IsItemExist(uint id) external view returns(bool) {
        return itemList[id].owner > 0;
    }

    //##########################################################
    //   Lock (Change the Ownership over the Item from Item to Token or Vice Versa):
    //    1. Assigns the Item ownership to the LORDS Token(item ids, hero id)
    //    2. Assigns the Ownership over the items to the Player.(hero id)
    //    3. Get List of Locked Items(hero  id)
    //##########################################################
    function LockItems(uint[5] memory ids, uint heroID) public {
        require(msg.sender == _contractOwner, "PERMISSION DENIED, Only Item Factory can change the Item Owner");
        require(heroID > 0, "Hero ID should be greater that");
        require(lockedList[heroID].owner == 0, "Hero has locked items already");

        uint itemOwner = 0;

        address heroOwnerAddress = IERC721(_heroToken).ownerOf(heroID);
        uint heroOwner = WalletlessI(_walletless).GetNumber(heroOwnerAddress);
        /* require(heroOwner > 0, "Hero was not found"); */

        for(uint i=0; i<5; i++) {
            if (ids[i] > 0) {
              require(createdList[ids[i]] == true, "Item does not exist");
              itemOwner = itemList[ids[i]].owner;
              require(itemOwner == heroOwner, "Item owner and hero owners are not matching");
            }
        }

        // No need to continue if there are no items to lock
        if (itemOwner == 0) {
          return;
        }

        // Map the Hero ID to the Items
        lockedList[heroID] = HeroOwned(heroOwner, ids);

        for(uint i=0; i<5; i++) {
            itemList[ids[i]].owner = 0;
        }

        string memory ownerID = WalletlessI(_walletless).GetID(heroOwnerAddress);
        emit ItemsLocked(heroID, ids, ownerID);
    }
    function UnlockItems(uint heroID) public {
        require(lockedList[heroID].owner > 0, "No locked items");

        address heroOwnerAddress = IERC721(_heroToken).ownerOf(heroID);
        uint heroOwner = WalletlessI(_walletless).GetNumber(heroOwnerAddress);
        require(heroOwner > 0, "Hero owner not found");

        require(msg.sender == _contractOwner || msg.sender == heroOwnerAddress, "PERMISSION DENIED, Only Item Factory or Hero Owner can unlock items");

        for(uint i=0; i<5; i++) {
            itemList[lockedList[heroID].items[i]].owner = heroOwner;
        }

        uint[5] memory ids = lockedList[heroID].items;

        delete lockedList[heroID];

        string memory ownerID = WalletlessI(_walletless).GetID(heroOwnerAddress);
        emit ItemsUnlocked(heroID, ids, ownerID);
    }
    function GetLockedItems(uint heroID) public view returns(uint, uint, uint, uint, uint, uint) {
      return (lockedList[heroID].owner, lockedList[heroID].items[0], lockedList[heroID].items[1],
        lockedList[heroID].items[2], lockedList[heroID].items[3], lockedList[heroID].items[4]);
    }

    //##########################################################
    //  Collector:
    //    1. Give Back the Item(item id, item owner address, sig...)
    //##########################################################

    function GiveBack(uint itemID, address ownerAddr, uint8 sigV, bytes32 sigR, bytes32 sigS) public
    {
      // validate the signature
      uint owner = WalletlessI(_walletless).GetNumber(ownerAddr);
      require(owner > 0, "No hero found");

      require(owner == itemList[itemID].owner, "Item not belong to the owner");

      require(CollectorI(_collector).IsCollectableItem(itemID), "Item not collectable");

      // Signature verification
      bytes32 digest = keccak256(abi.encodePacked(
          "\x19\x01",
          DOMAIN_SEPARATOR_BACK,
          GetGivingBackHash(itemID, nonceList[owner])
      ));
      require(Verify(digest, ownerAddr, sigV, sigR, sigS), "Verification Failed");

      delete itemList[itemID];

      nonceList[owner] = nonceList[owner] + 1;

      string memory ownerID = WalletlessI(_walletless).GetID(ownerAddr);
      emit ItemCollected(itemID, ownerID);
    }

    //##########################################################
    //
    //  Misc:
    //    1. Do not accept any asset
    //##########################################################

    function () external {}

    constructor(uint chainId) public {
          DOMAIN_SEPARATOR_GRANT  = Hash(EIP712Domain({
              name: "Grant Permission to the Broker",
              version: '1',
              chainId: chainId,
              //verifyingContract: address(this)
              verifyingContract: 0x5E72914535f202659083Db3a02C984188Fa26e9f
          }));
          DOMAIN_SEPARATOR_REFUSE = Hash(EIP712Domain({
              name: "Refuse the Permission to the broker",
              version: '1',
              chainId: chainId,
              verifyingContract: 0x5E72914535f202659083Db3a02C984188Fa26e9f
          }));
          DOMAIN_SEPARATOR_BACK   = Hash(EIP712Domain({
              name: "Give Back Item (Will be burnt)",
              version: '1',
              chainId: chainId,
              verifyingContract: 0x5E72914535f202659083Db3a02C984188Fa26e9f
          }));
    }


    //##########################################################
    //   Signature (EIP-712 standard):
    //##########################################################

    struct EIP712Domain {
        string  name;
        string  version;
        uint256 chainId;
        address verifyingContract;
    }

    bytes32 constant public EIP712DOMAINTYPE_HASH = keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)");

    bytes32 constant public TXTYPE_HASH_GRANT   = keccak256("GrantOwnership(uint itemID,uint nonce)");
    bytes32 constant public TXTYPE_HASH_REFUSE  = keccak256("RefuseOwnership(uint itemID,uint nonce)");
    bytes32 constant public TXTYPE_HASH_BACK    = keccak256("GiveBack(uint itemID,uint nonce)");

    bytes32 public DOMAIN_SEPARATOR_GRANT;
    bytes32 public DOMAIN_SEPARATOR_REFUSE;
    bytes32 public DOMAIN_SEPARATOR_BACK;

    function GetGrantingHash(uint itemID, uint nonce) internal pure returns (bytes32) {
      return keccak256(abi.encode(
        TXTYPE_HASH_GRANT,
        itemID,
        nonce
      ));
    }
    function GetRefusingHash(uint itemID, uint nonce) internal pure returns (bytes32) {
      return keccak256(abi.encode(
        TXTYPE_HASH_REFUSE,
        itemID,
        nonce
      ));
    }
    function GetGivingBackHash(uint itemID, uint nonce) internal pure returns (bytes32) {
      return keccak256(abi.encode(
        TXTYPE_HASH_BACK,
        itemID,
        nonce
      ));
    }

    function Hash(EIP712Domain memory eip712Domain) internal pure returns (bytes32) {
        bytes memory encoded = abi.encode(
            EIP712DOMAINTYPE_HASH,
            keccak256(bytes(eip712Domain.name)),
            keccak256(bytes(eip712Domain.version)),
            eip712Domain.chainId,
            eip712Domain.verifyingContract
        );


        return keccak256(encoded);
    }

    function Verify(bytes32 digest, address signer, uint8 v, bytes32 r, bytes32 s) internal pure returns (bool) {
        // Note: we need to use `encodePacked` here instead of `encode`.
        return ecrecover(digest, v, r, s) == signer;
    }
}
