pragma solidity ^0.5.8;

contract Walletless {

    uint number;            // registered users number

    struct User {
        address primary;
        address temporary;
        string ID;
    }

    struct TemporaryUpdating {
        address temporary;
        string ID;
    }

    struct PrimaryUpdating {
      address temporary;
      address primary;
    }

    address public gameOwner = msg.sender;

    uint public threshold;                      // immutable state
    mapping (address => uint) public temporaryAddresses; // mutable state
    mapping (address => uint) public primaryAddresses; // mutable state
    mapping (string => uint) ids;           // Email to User ID
    mapping (uint => User) public users;               // User ID to User data

    // EIP712 Precomputed hashes:
    struct EIP712Domain {
        string  name;
        string  version;
        uint256 chainId;
        address verifyingContract;
    }

    bytes32 constant public EIP712DOMAINTYPE_HASH = keccak256(
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
    );

    bytes32 constant public TXTYPE_HASH_TEMPORARY = keccak256(
        "UpdateTemporary(address temporary,string memory id)"
    );
    bytes32 constant public TXTYPE_HASH_PRIMARY = keccak256(
        "UpdatePrimary(address temporary,address primary)"
    );

    bytes32 public DOMAIN_SEPARATOR_TEMPORARY;          // hash for EIP712, computed from contract address
    bytes32 public DOMAIN_SEPARATOR_PRIMARY;            // hash for EIP712, computed from contract address

    // Note that owners_ must be strictly increasing, in order to prevent duplicates
    constructor(uint chainId) public {
        threshold = 2;
        number = 0;

        DOMAIN_SEPARATOR_PRIMARY = hash(EIP712Domain({
            name: "Update Primary Address",
            version: '1',
            chainId: chainId,
            //verifyingContract: address(this)
            verifyingContract: 0x5E72914535f202659083Db3a02C984188Fa26e9f
        }));

        DOMAIN_SEPARATOR_TEMPORARY = hash(EIP712Domain({
            name: "Update Temporary Address",
            version: '1',
            chainId: chainId,
            verifyingContract: 0x5E72914535f202659083Db3a02C984188Fa26e9f
            //verifyingContract: address(this)
        }));
    }

    function hash(EIP712Domain memory eip712Domain) internal returns (bytes32) {

        bytes memory encoded = abi.encode(
            EIP712DOMAINTYPE_HASH,
            keccak256(bytes(eip712Domain.name)),
            keccak256(bytes(eip712Domain.version)),
            eip712Domain.chainId,
            eip712Domain.verifyingContract
        );


        return keccak256(encoded);
    }

    function getTemporaryHash(address temporary, string memory ID) internal pure returns (bytes32) {
      return keccak256(abi.encode(
        temporary,
        keccak256(bytes(ID))
      ));
    }

    function getPrimaryHash(address temporary, address primary) internal pure returns (bytes32) {
      return keccak256(abi.encode(
        TXTYPE_HASH_PRIMARY,
        temporary,
        primary
      ));
    }

    function verify(bytes32 digest, address signer, uint8 v, bytes32 r, bytes32 s) internal pure returns (bool) {
        // Note: we need to use `encodePacked` here instead of `encode`.
        return ecrecover(digest, v, r, s) == signer;
    }

    /// Add a temporary address to the account
    function UpdateTemporary(address temporary, string memory ID, uint8 sigV, bytes32 sigR, bytes32 sigS) public {
        /* require(sigR.length == threshold); */
        /* require(sigR.length == sigS.length && sigR.length == sigV.length); */

        require(temporaryAddresses[temporary] == 0, "Address is used as a temporary account");
        require(primaryAddresses[temporary] == 0, "Address is used as a primary account");
        require(bytes(ID).length <= 254, "ID length must not exceed 254 characters");

        bytes32 digest = getTemporaryHash(temporary, ID);
        emit logBytes32(digest);

        /* assert(verify(digest, temporary, sigV[0], sigR[0], sigS[0])); */

        // if id exists,
        // remove the temporary account
        // update the user's temporary account
        /* uint userNumber = ids[ID];
        if (userNumber > 0) {
            User storage user = users[userNumber];

            address oldTemporary = user.temporary;
            if (temporaryAddresses[oldTemporary] > 0) {
                delete temporaryAddresses[oldTemporary];
            }

            user.temporary = temporary;
        }
        else {
            number = number + 1;
            userNumber = number;

            users[userNumber] = User(temporary, address(0), ID);
        }

        // register the temporary account
        temporaryAddresses[temporary] = userNumber; */
    }

    event logString(string str);
    event logBytes32(bytes32 bts);
    event logBytes(bytes bts);

    function LogTemporaryHash(address temporary, string memory ID) public returns (bytes32) {
        bytes memory encoded = abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR_PRIMARY,
            getTemporaryHash(temporary, ID)
        );

        bytes32 digest = keccak256(encoded);

        emit logBytes(encoded);

        return digest;
    }


    function stringToBytes(string memory ID) public pure returns (bytes memory) {
        bytes memory id = bytes(ID);

        return id;
    }



    // Add a primary wallet address (account) of a user.
    // This operation is valid once only. After adding primary address, player can not change it later
    // So in future version we could change that
    //
    // requirements to update the primary address is to have an temporary address with ID
    function UpdatePrimary(address temporary, address primary, uint8[] memory sigV, bytes32[] memory sigR, bytes32[] memory sigS) public {
        require(temporaryAddresses[temporary] > 0, "Temporary address not exists");
        require(temporaryAddresses[primary] == 0, "Address is already used as a temporary account");
        require(primaryAddresses[primary] == 0, "Address is already used as a primary account");

        require(sigR.length == threshold);
        require(sigR.length == sigS.length && sigR.length == sigV.length);

        // if id exists,
        // remove the temporary account
        // update the user's temporary account
        uint userNumber = temporaryAddresses[temporary];
        require(users[userNumber].primary == address(0), "Primary address exists for the user already");

        bytes32 digest = keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR_PRIMARY,
            getPrimaryHash(temporary, primary)
        ));

        assert(verify(digest, temporary, sigV[0], sigR[0], sigS[0]));
        assert(verify(digest, gameOwner, sigV[1], sigR[1], sigS[1]));

        users[userNumber].primary = primary;

        // register the primary account
        primaryAddresses[primary] = userNumber;
    }


    // Can update the ID if primary address is added.
    // Only primary address hold can invoke the function
    function UpdateID(string memory id_) public {
        require(primaryAddresses[msg.sender] > 0, "Primary address not found. Required to be set the primary address");
        require(ids[id_] == 0, "ID is used already");
        require(bytes(id_).length <= 254, "ID length must not exceed 254 characters");

        uint userNumber = primaryAddresses[msg.sender];

        // remove the previous id
        delete ids[users[userNumber].ID];

        users[userNumber].ID = id_;
    }

    /// Returns User.ID by temporary or primary account used by the player
    function GetID(address account) public view returns(string memory) {
        uint userNumber = primaryAddresses[account];
        if (userNumber == 0) {
            userNumber = temporaryAddresses[account];

            if (userNumber == 0) {
                return "";
            }
        }

        return users[userNumber].ID;
    }

    function GetTemporary(string memory id_) public view returns (address) {
        uint userNumber = ids[id_];
        if (userNumber == 0) {
            return address(0);
        }
        return users[userNumber].temporary;
    }

    function GetPrimary(string memory id_) public view returns (address) {
        uint userNumber = ids[id_];
        if (userNumber == 0) {
            return address(0);
        }
        return users[userNumber].primary;
    }
}
